import React from "react";
//Imports
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faLinkedinIn } from "@fortawesome/free-brands-svg-icons";
import { faTwitter } from "@fortawesome/free-brands-svg-icons";
import { faEnvelope } from "@fortawesome/free-solid-svg-icons";
import copy from "copy-to-clipboard";
//Types
import { SocialMedia } from "../Types/Types";
//Styles
import "./ContactMe.css";

const ContactMe = () => {
	const [isCopied, set_isCopied] = React.useState(0);

	const copy_to_clipboard = () => {
		copy("justincabrera854@gmail.com", {
			onCopy: () => set_isCopied(1),
		});
	};

	const goTo = (link: string) => {
		window.open(link);
	};
	return (
		<div className='contact-container'>
			<header>
				<h1>You can find me in the following platforms</h1>
			</header>
			<ul>
				<li
					className='linkedin'
					onClick={() =>
						goTo("https://www.linkedin.com/in/justin-cabrera-a725001a2/")
					}>
					<FontAwesomeIcon
						icon={faLinkedinIn}
						size='2x'
						className='icon-contact'
					/>{" "}
					<span>Justin Cabrera</span>
				</li>
				<li
					className='twitter'
					onClick={() => goTo("https://twitter.com/Hareds_")}>
					<FontAwesomeIcon
						icon={faTwitter}
						size='2x'
						className='icon-contact'
					/>
					<span>Hareds_</span>
				</li>
				<li
					className='gmail'
					id='gmail'
					onClick={copy_to_clipboard}
					data-gmail-isCopied={isCopied}>
					<FontAwesomeIcon
						icon={faEnvelope}
						size='2x'
						className='icon-contact'
					/>
					<span>justincabrera854@gmail.com</span>
				</li>
			</ul>
		</div>
	);
};

export default ContactMe;
