import React, { MouseEventHandler } from "react";
//Styles
import "./Icons.css";
type props = {
	title: string;
};

const Icons: React.FC<props> = ({ title }) => {
	const icon_container = React.useRef<HTMLDivElement>(null);
	const test = (e: React.MouseEvent<HTMLDivElement, MouseEvent>) => {
		if (!icon_container.current) return;
		icon_container.current.style.scale = "1.1";
	};

	return (
		<div
			className='icon-container'
			ref={icon_container}
			onClick={(event) => test(event)}>
			<span>{title}</span>
		</div>
	);
};

export default Icons;
