import React, { useEffect, useRef, useCallback } from "react";
import * as THREE from "three";
//Local Components
import Header from "./Header/Header";
//Styles
import "./App.css";

const App = () => {
	const initialWidth = window.innerWidth;
	const initialHeight = window.innerHeight;

	const scene = new THREE.Scene();
	const camera = new THREE.PerspectiveCamera(
		90,
		initialWidth / initialHeight,
		0.1,
		350
	);
	var tanFOV = Math.tan(((Math.PI / 180) * camera.fov) / 2);
	const renderer = useRef(new THREE.WebGL1Renderer());

	const appDiv = useCallback(
		(div: HTMLDivElement) => {
			if (div == null) return;
			const canvas = document.getElementById("city-canvas");
			canvas?.remove();
			renderer.current.setSize(initialWidth, initialHeight);
			renderer.current.domElement.id = "city-canvas";
			div.appendChild(renderer.current.domElement);
		},
		[renderer]
	);
	//afdsfas
	//Cube
	const geometry = new THREE.BoxGeometry(2, 2, 2);
	const material = new THREE.MeshBasicMaterial({ color: 0x00ff00 });
	const cube = new THREE.Mesh(geometry, material);
	scene.add(cube);
	camera.position.z = 120;
	//Animation and Rendering the Cube
	const animateCube = () => {
		requestAnimationFrame(animateCube);
		cube.rotation.x += 0.01;
		cube.rotation.y += 0.01;
		renderer.current.render(scene, camera);
	};

	//Update the dimensions of the Renderer
	const updateWindowDimensions = () => {
		camera.aspect = window.innerWidth / window.innerHeight;
		camera.fov =
			(360 / Math.PI) *
			Math.atan(tanFOV * (window.innerHeight / initialHeight));
		camera.updateProjectionMatrix();
		camera.lookAt(scene.position);
		renderer.current.setSize(window.innerWidth, window.innerHeight);
	};

	animateCube();
	//Update Size of Window
	useEffect(() => {
		window.addEventListener("resize", updateWindowDimensions);
	});
	//Initialize the Renderer
	useEffect(() => {}, []);

	return (
		<div className='App' ref={appDiv}>
			<Header />
		</div>
	);
};

export default App;
