import React, { useEffect, useRef } from "react";
import * as THREE from "three";

import "./App.css";

const App = () => {
	const width = useRef(window.innerWidth);
	const height = useRef(window.innerHeight);
	const appDiv = useRef<HTMLDivElement>(null);

	const scene = new THREE.Scene();
	const camera = new THREE.PerspectiveCamera(
		90,
		width.current / height.current,
		0.1,
		1000
	);
	const renderer = new THREE.WebGL1Renderer();
	//Cube
	const geometry = new THREE.BoxGeometry(2, 2, 2);
	const material = new THREE.MeshBasicMaterial({ color: 0x00ff00 });
	const cube = new THREE.Mesh(geometry, material);

	scene.add(cube);
	camera.position.z = 5;
	//Animation and Rendering the Cube
	const animateCube = () => {
		requestAnimationFrame(animateCube);
		cube.rotation.x += 0.01;
		cube.rotation.y += 0.01;
		renderer.render(scene, camera);
	};

	//Update the dimensions of the Renderer
	const updateWindowDimensions = () => {
		width.current = window.innerWidth;
		height.current = window.innerHeight;
		renderer.setSize(width.current, height.current);
	};

	animateCube();
	//Update Size of Window
	useEffect(() => {
		window.addEventListener("resize", updateWindowDimensions);
	});
	//Initialize the Renderer
	useEffect(() => {
		renderer.setSize(width.current, height.current);
		document.body.appendChild(renderer.domElement);
	}, []);

	return (
		<div className='App' ref={appDiv}>
			<div className='info'>Description</div>
		</div>
	);
};

export default App;
