import React from "react";
//Imports
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faLinkedinIn } from "@fortawesome/free-brands-svg-icons";
import { faTwitter } from "@fortawesome/free-brands-svg-icons";
import { faEnvelope } from "@fortawesome/free-solid-svg-icons";
import copy from "copy-to-clipboard";
//Types
import { SocialMedia } from "../Types/Types";
//Styles
import "./ContactMe.css";

const ContactMe = () => {
	const platform_hover_details = React.useRef<HTMLDivElement>(null);

	const copy_to_clipboard = () => {
		if (platform_hover_details.current === null) return;
		copy("justincabrera854@gmail.com");
		platform_hover_details.current.style.display = "block";
		//Testing
		let count = 0;
		setInterval(() => console.log(count++), 1000);
		setTimeout((platform_hover_details.current.style.display = "none"), 10000);
	};

	const goTo = (link: string) => {
		window.open(link);
	};
	return (
		<div className='contact-container'>
			<header>
				<h1>You can find me in the following platforms</h1>
			</header>
			<ul>
				<li
					className='linkedin'
					onClick={() =>
						goTo("https://www.linkedin.com/in/justin-cabrera-a725001a2/")
					}>
					<div className='platform-data'>
						<FontAwesomeIcon
							icon={faLinkedinIn}
							size='2x'
							className='icon-contact'
						/>{" "}
						<span>Justin Cabrera</span>
					</div>
				</li>
				<li
					className='twitter'
					onClick={() => goTo("https://twitter.com/Hareds_")}>
					<div className='platform-data'>
						<FontAwesomeIcon
							icon={faTwitter}
							size='2x'
							className='icon-contact'
						/>
						<span>Hareds_</span>
					</div>
				</li>
				<li className='gmail' id='gmail' onClick={copy_to_clipboard}>
					<div className='platform-data'>
						<FontAwesomeIcon
							icon={faEnvelope}
							size='2x'
							className='icon-contact'
						/>
						<span>justincabrera854@gmail.com</span>
					</div>
					<div className='platform-hover-details' ref={platform_hover_details}>
						{" "}
						Copied to clipboard
					</div>
				</li>
			</ul>
		</div>
	);
};

export default ContactMe;
