import React, { useState, useEffect, useRef } from "react";
import { Link, NavLink } from "react-router-dom";
//Styles
import "./Header.css";
type Navigation = {
  name: string;
  path: string;
  id: string;
};

const Header = () => {
  const nav: Navigation[] = [
    { name: "Home", path: "/home", id: "home" },
    { name: "Work", path: "/work", id: "portfolio" },
    { name: "About", path: "/about", id: "about" },

    {
      name: "Contact",
      path: "/contact",
      id: "contact-info",
    },
  ];
  const [header_isVisible, set_header_isVisible] = useState(true);
  const [current, set_current] = useState<Navigation>();

  //On load Header Timeout
  useEffect(() => {
    set_current(nav[0]);

    setTimeout(() => {
      set_header_isVisible(true);
    }, 5000);
  }, []);

  return (
    <div
      className="header"
      style={
        header_isVisible ? { visibility: "visible" } : { visibility: "hidden" }
      }
    >
      <div className="navigation">
        {nav.map((element) => {
          return (
            <Link
              to={element.path}
              className={`header-nav${current === element ? "-isActive" : ""}`}
              onClick={() => set_current(element)}
            >
              <b>{element.name}</b>
            </Link>
          );
        })}
      </div>
    </div>
  );
};

export default Header;
