import React from "react";
//Styles
import "./Budget.css";

const Budget = () => {
  const [slider_one_value, set_slider_one_value] = React.useState<number>(50);
  const [slider_two_value, set_slider_two_value] = React.useState<number>(500);

  const gap = 0;
  //   React.useEffect(() => {
  //     if (slider_two_value - slider_one_value <= gap) {
  //       set_slider_one_value(slider_two_value - gap);
  //     }
  //   }, [slider_one_value]);
  //   React.useEffect(() => {
  //     if (slider_one_value - slider_two_value <= gap) {
  //       set_slider_two_value(slider_one_value - gap);
  //     }
  //   }, [slider_two_value]);

  return (
    <div className="budget-container">
      <h4>Budget</h4>
      <div className="budget-wrapper">
        <div className="slider_tracker"></div>
        <input
          className="budget-slider"
          type="range"
          min="50"
          max="5000"
          value={slider_one_value}
          onChange={(e) => {
            set_slider_one_value(parseInt(e.target.value));
          }}
          id="slider-1"
        />
        <input
          className="budget-slider"
          type="range"
          min="50"
          max="5000"
          value={slider_two_value}
          onChange={(e) => {
            set_slider_two_value(parseInt(e.target.value));
          }}
          id="slider-2"
        />
      </div>
    </div>
  );
};

export default Budget;
