import React, { useEffect, useState } from "react";
import { Node } from "./Pathfinder";
import Element from "./Node";
import "./styles.css";

const START_NODE_ROW = 0;
const START_NODE_COLUMN = 0;

const END_NODE_ROW = 9;
const END_NODE_COLUMN = 14;

const open_set = new Array<Node>();
const closed_set = [];

const Container3 = () => {
  const [grid, set_grid] = useState(new Array<Node[]>());

  const algorithm = () => {
    while (open_set.length > 0) {
      const end = grid[END_NODE_ROW][END_NODE_COLUMN];
      let lowestIndex = 0;
      //CHeck for the lowest "F" value
      for (let i = 0; i < open_set.length; i++) {
        if (open_set[i].f < open_set[lowestIndex].f) {
          lowestIndex = i;
        }
      }

      let current = open_set[lowestIndex];
      if (current === end) {
        console.log("done");
        return;
      }
      closed_set.push(current); // Add current to the closed list
      remove_from_array(open_set, current); // Remove current from Open Set
    }
  };

  const addNeighbours = () => {
    for (let row = 0; row < grid.length; row++) {
      for (let col = 0; col < grid.length; col++) {
        grid[row][col].createNeighbours(grid);
      }
    }
  };

  useEffect(() => {
    set_grid(createGrid());
    addNeighbours();
  }, []);

  //   useEffect(() => {
  //     console.log(grid);
  //   }, [grid]);
  return (
    <div className="main-container">
      <div className="Grid">
        {grid.map((node) => {
          return node.map((node) => {
            return <Element node={node} />;
          });
        })}
      </div>
    </div>
  );
};

const createGrid = () => {
  const grid = [];
  for (let row = 0; row < 10; row++) {
    const currentRow = [];
    for (let column = 0; column < 15; column++) {
      const isStart = row === START_NODE_ROW && column === START_NODE_COLUMN;
      const isFinish = row === END_NODE_ROW && column === END_NODE_COLUMN;
      const isWall = false;
      const node = new Node(row, column, isStart, isFinish, isWall);
      currentRow.push(node);
    }
    grid.push(currentRow);
    open_set.push(grid[START_NODE_ROW][START_NODE_COLUMN]);
  }
  return grid;
};

const remove_from_array = (arr: Array<Node>, item: Node) => {
  for (let i = arr.length - 1; i >= 0; i--) {
    if (arr[i] == item) {
      arr.splice(i, 1);
    }
  }
};
export default Container3;
