import React, { useEffect, useRef, useCallback } from "react";
import * as THREE from "three";
import * as dat from "dat.gui";
//Local Components
import Header from "./Header/Header";
//Styles
import "./App.css";

const App = () => {
	//Window Information
	const initialWidth = window.innerWidth;
	const initialHeight = window.innerHeight;

	//Debug
	let gui = useRef(new dat.GUI());

	//Three Initialization
	const scene = new THREE.Scene();
	const camera = new THREE.PerspectiveCamera(
		90,
		initialWidth / initialHeight,
		0.1,
		150
	);
	camera.position.z = 10;
	let tanFOV = Math.tan(((Math.PI / 180) * camera.fov) / 2);

	const renderer = useRef(new THREE.WebGL1Renderer());

	const appDiv = useCallback(
		//Surround the canvas inside div
		(div: HTMLDivElement) => {
			if (div == null) return;
			const canvas = document.getElementById("city-canvas");
			const guiDiv = document.getElementsByClassName("dg main a");
			if (guiDiv.length > 1) guiDiv[guiDiv.length - 1].remove();
			canvas?.remove();
			console.log(guiDiv);
			//guiDiv?.remove();
			//guiDivPlane?.remove();

			renderer.current.setSize(initialWidth, initialHeight); //Build Renderers
			renderer.current.domElement.id = "city-canvas"; //Add Rendered inside div
			div.appendChild(renderer.current.domElement);
		},
		[renderer]
	);
	//========================		OBJECTS	(START)		=========================

	//Cube
	const cubeGeometry = new THREE.BoxGeometry(2, 2, 2); //Object
	const material = new THREE.MeshBasicMaterial({ color: 0x00ff00 }); //Material
	const cube = new THREE.Mesh(cubeGeometry, material); //Create Mesh
	//scene.add(cube); //Add cube to the scene
	//Animation and Rendering  of the Cube
	const animateCube = () => {
		requestAnimationFrame(animateCube);
		cube.rotation.x += 0.01;
		cube.rotation.y += 0.01;
		renderer.current.render(scene, camera);
	};
	animateCube();

	//Object x
	const xGeometry = new THREE.PlaneBufferGeometry(3, 3, 64, 64);
	const xMaterial = new THREE.MeshStandardMaterial({ color: "white" });
	const xMesh = new THREE.Mesh(xGeometry, xMaterial);
	xMesh.rotation.x = 181;
	scene.add(xMesh);
	gui.current.add(xMesh.rotation, "x").min(0).max(200);
	//========================		OBJECTS	(END)		========================

	//========================		LIGHTS (START)	 	=========================
	const firstLight = new THREE.PointLight(0x42a5f5, 2, 100);
	firstLight.position.set(2, 3, 4);
	scene.add(firstLight);

	//========================		LIGHTS (END)			========================

	//========================		USE EFFECT (START)	 	========================
	//Update the dimensions of the Renderer
	const updateWindowDimensions = () => {
		camera.aspect = window.innerWidth / window.innerHeight;
		camera.fov =
			(360 / Math.PI) *
			Math.atan(tanFOV * (window.innerHeight / initialHeight));
		camera.updateProjectionMatrix();
		camera.lookAt(scene.position);
		renderer.current.setSize(window.innerWidth, window.innerHeight);
	};

	//Update elements as the Size of Window changes
	useEffect(() => {
		window.addEventListener("resize", updateWindowDimensions);
	});
	//Initialize the Renderer

	return (
		<div className='App' ref={appDiv}>
			<Header />
		</div>
	);
};

export default App;
